import React, { useEffect, useRef, useState } from 'react';
import './ImageRevealOnScroll.css';
import img1 from './../../Assests/backimg.jpg';
const ImageRevealOnScroll = () => {
    const [isVisible, setIsVisible] = useState(false);
    const imageRef = useRef(null);

    const handleScroll = () => {
        if (imageRef.current) {
            const rect = imageRef.current.getBoundingClientRect();
            const isInView = rect.top < window.innerHeight && rect.bottom >= 0;
            setIsVisible(isInView);
        }
    };

    useEffect(() => {
        window.addEventListener('scroll', handleScroll);
        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, []);

    return (
        <div className="image-container">
            <div className={`image ${isVisible ? 'visible' : ''}`} ref={imageRef}>
                <img src={img} alt="Scroll to reveal" />
            </div>
        </div>
    );
};

export default ImageRevealOnScroll;
