import React, { useEffect, useRef, useState } from 'react';
import './ImageRevealOnScroll.css';
import img1 from './Assests/backimg';
const ImageRevealOnScroll = () => {
    const [isVisible, setIsVisible] = useState(false);
    const imageRef = useRef();

    useEffect(() => {
        const observer = new IntersectionObserver(
            ([entry]) => {
                setIsVisible(entry.isIntersecting);
            },
            { threshold: 0.1 }
        );

        if (imageRef.current) {
            observer.observe(imageRef.current);
        }

        return () => {
            if (imageRef.current) {
                observer.unobserve(imageRef.current);
            }
        };
    }, []);

    return (
        <div className="image-container">
            <div className={`image ${isVisible ? 'visible' : ''}`} ref={imageRef}>
                <img src={img1} alt="Scroll to reveal" />
            </div>
        </div>
    );
};

export default ImageRevealOnScroll;
