import React, { useState, useRef, useEffect } from 'react';
import { Link } from 'react-router-dom';
import navCSS from './Nav.module.css';

export default function Nav() {
    const [isMenuVisible, setIsMenuVisible] = useState(false);
    const menuRef = useRef();
    const navbar = useRef();

    const menuHandler = () => {
        setIsMenuVisible((prev) => !prev);
    };

    useEffect(() => {
        const handleScroll = () => {
            if (navbar.current) {
                if (window.scrollY > 100) {
                    navbar.current.classList.add(navCSS.showNav);
                } else {
                    navbar.current.classList.remove(navCSS.showNav);
                }
            }
        };

        window.addEventListener('scroll', handleScroll);
        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, []);

    const handleClickOutside = (event) => {
        if (menuRef.current && !menuRef.current.contains(event.target)) {
            setIsMenuVisible(false);
        }
    };

    useEffect(() => {
        document.addEventListener('mousedown', handleClickOutside);
        return () => {
            document.removeEventListener('mousedown', handleClickOutside);
        };
    }, []);

    return (
        <nav className={navCSS.nav_wrapper} ref={navbar}>
            <div className={navCSS.logo}>
                <Link to="/"><span>ShreeJi</span></Link>
            </div>
            <ul ref={menuRef} className={`${navCSS.menu} ${isMenuVisible ? navCSS.showNav : ''}`}>
                <li><Link to="/">Home</Link></li>
                <li><Link to="/about">About Us</Link></li>
                <li><Link to="/categories">Attraction</Link></li>
                <li><Link to="/rooms">Rooms</Link></li>
                <li><Link to="/testimonials">Testimonials</Link></li>
                {/* <li><Link to="/blogs">Blogs</Link></li> */}
            </ul>
            <div className={navCSS.Nav_btns}>
                <button>Book Now</button>
                <i 
                    className='ri-menu-4-line' 
                    id={navCSS.bars} 
                    onClick={menuHandler} 
                    aria-expanded={isMenuVisible}
                    aria-label="Toggle navigation menu"
                ></i>
            </div>
        </nav>
    );
}
