import React from "react";
import roomCSS from './../Rooms/Room.module.css';

const rooms = [
    { name: "Junior Suite", price: "$120/N", image: require("./../../Assests/backimg.jpg"), description: ["Daily Cleaning", "Home Service", "HouseKeeping", "Wifi & Parking"] },
    { name: "Twin Room", price: "$120/N", image: require("./../../Assests/r3.jpg"), description: ["Daily Cleaning", "Home Service", "HouseKeeping", "Wifi & Parking"] },
    { name: "Quad Room", price: "$150/N", image: require("./../../Assests/r4.jpg"), description: ["Daily Cleaning", "Home Service", "HouseKeeping", "Wifi & Parking"] },
    { name: "Deulux Room", price: "$200/N", image: require("./../../Assests/r5.jpg"), description: ["Daily Cleaning", "Home Service", "HouseKeeping", "Wifi & Parking"] },
    { name: "Exclusive Room", price: "$120/N", image: require("./../../Assests/r6.jpg"), description: ["Daily Cleaning", "Home Service", "HouseKeeping", "Wifi & Parking"] },
    { name: "Personal Room", price: "$120/N", image: require("./../../Assests/backimg.jpg"), description: ["Daily Cleaning", "Home Service", "HouseKeeping", "Wifi & Parking"] }
];

function Rooms() {
    return (
        <div className={`${roomCSS.Rooms_container} section`}>
            <small className="section_Heading">Luxury Suits</small>
            <h2 className="section_Title">Our Best <span>Rooms</span></h2>
            <div className={roomCSS.cards}>
                {rooms.map((room, index) => (
                    <div key={index} className={roomCSS.card_Container}>
                        <div className={roomCSS.card}>
                            <div 
                                className={`${roomCSS.Card_front}`}
                                style={{ backgroundImage: `url(${room.image})` }}
                            >
                                <button>{room.name}</button>
                            </div>
                            <div className={roomCSS.Card_Back}>
                                <div className={roomCSS.price}>
                                    <p>{room.price}</p>
                                </div>
                                <div className={roomCSS.Card_content}>
                                    <h3>{room.name}</h3>
                                    {room.description.map((desc, i) => <p key={i}> - {desc}</p>)}
                                </div>
                                <div className={roomCSS.BookNow}>
                                    <button>Book Now</button>
                                    <i className="ri-arrow-right-line"></i>
                                </div>
                            </div>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default Rooms;
